mapper.select:
select count(*) from products

	where name like #{keyword}


	where contents like #{keyword}

select * from products

where name like #{keyword}


where contents like #{keyword}

order by num desc

mapper.update:
update products set 
name=#{name}, company=#{company}, image=#{image},
stock=#{stock},price=#{price},
contents=#{contents},point=#{point}
where num = #{num}
